import React, { Component } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

export default class Createexpense extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeDuration = this.onChangeDuration.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      expensename: '',
      description: '',
      duration: 0,
      date: new Date(),
      expenses: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/expenses/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            expenses: response.data.map(expense => expense.expensename),
            expensename: response.data[0].expensename
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeUsername(e) {
    this.setState({
      expensename: e.target.value
    })
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    })
  }

  onChangeDuration(e) {
    this.setState({
      duration: e.target.value
    })
  }

  onChangeDate(date) {
    this.setState({
      date: date
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const expense = {
      expensename: this.state.expensename,
      description: this.state.description,
      duration: this.state.duration,
      date: this.state.date
    }

    console.log(expense);

    axios.post('http://localhost:5000/expenses/add', expense)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Create New expense Log</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>expensename: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.username}
              onChange={this.onChangeUsername}>
              {
                this.state.expenses.map(function(expense) {
                  return <option 
                    key={expense}
                    value={expense}>{expense}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="form-group"> 
          <label>Description: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.description}
              onChange={this.onChangeDescription}
              />
        </div>
        <div className="form-group">
          <label>Duration (in minutes): </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.duration}
              onChange={this.onChangeDuration}
              />
        </div>
        <div className="form-group">
          <label>Date: </label>
          <div>
            <DatePicker
              selected={this.state.date}
              onChange={this.onChangeDate}
            />
          </div>
        </div>

        <div className="form-group">
          <input type="submit" value="Create expense Log" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
